import React, { useState, useEffect } from 'react';
import { db } from '../../firebase/config';
import { collection, addDoc, getDocs, deleteDoc, doc, query, where } from 'firebase/firestore';
import './Dashboard.css';

const Dashboard = () => {
    const [activeSection, setActiveSection] = useState('orders');
    const [showCustomInput, setShowCustomInput] = useState(false);
    const [customProductName, setCustomProductName] = useState('');
    const [customProducts, setCustomProducts] = useState({
        games: [],
        subscriptions: [],
        services: []
    });
    const [formData, setFormData] = useState({
        productName: '',
        costPrice: '',
        sellingPrice: '',
        description: '',
        serviceType: 'games', // ÿßŸÑŸÇŸäŸÖÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
        subType: '' // ŸÜŸàÿπ ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÅÿ±ÿπŸä
    });

    const sections = {
        orders: { title: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™', icon: 'üì¶' },
        capital: { title: 'ÿ±ÿ£ÿ≥ ÿßŸÑŸÖÿßŸÑ', icon: 'üí∞' },
        analytics: { title: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™', icon: 'üìä' },
        archive: { title: 'ÿßŸÑÿ£ÿ±ÿ¥ŸäŸÅ', icon: 'üìÇ' }
    };

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form submitted:', formData);
    };

    // ÿ•ÿ∂ÿßŸÅÿ© useEffect ŸÑÿ¨ŸÑÿ® ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿßŸÑŸÖÿÆÿµÿµÿ© ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ
    useEffect(() => {
        const fetchCustomProducts = async () => {
            try {
                const customProductsRef = collection(db, 'customProducts');
                const querySnapshot = await getDocs(customProductsRef);
                const products = {
                    games: [],
                    subscriptions: [],
                    services: []
                };

                querySnapshot.forEach((doc) => {
                    const product = { id: doc.id, ...doc.data() };
                    products[product.category].push(product);
                });

                setCustomProducts(products);
            } catch (error) {
                console.error("Error fetching custom products:", error);
            }
        };

        fetchCustomProducts();
    }, []);

    const handleCustomProductAdd = async () => {
        if (customProductName.trim()) {
            try {
                // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿ•ŸÑŸâ Firestore
                const customProductsRef = collection(db, 'customProducts');
                const newProduct = {
                    name: customProductName.trim(),
                    category: formData.serviceType,
                    createdAt: new Date()
                };

                const docRef = await addDoc(customProductsRef, newProduct);
                
                // ÿ™ÿ≠ÿØŸäÿ´ state ÿßŸÑŸÖÿ≠ŸÑŸä
                setCustomProducts(prev => ({
                    ...prev,
                    [formData.serviceType]: [
                        ...prev[formData.serviceType],
                        { id: docRef.id, ...newProduct }
                    ]
                }));

                setCustomProductName('');
                setShowCustomInput(false);
            } catch (error) {
                console.error("Error adding custom product:", error);
            }
        }
    };

    const handleCustomProductDelete = async (productId) => {
        try {
            // ÿ≠ÿ∞ŸÅ ÿßŸÑŸÖŸÜÿ™ÿ¨ ŸÖŸÜ Firestore
            await deleteDoc(doc(db, 'customProducts', productId));

            // ÿ™ÿ≠ÿØŸäÿ´ state ÿßŸÑŸÖÿ≠ŸÑŸä
            setCustomProducts(prev => ({
                ...prev,
                [formData.serviceType]: prev[formData.serviceType].filter(p => p.id !== productId)
            }));

            // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑŸÇŸäŸÖ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿ≠ÿ∞ŸàŸÅ ŸáŸà ÿßŸÑŸÖÿ≠ÿØÿØ ÿ≠ÿßŸÑŸäÿßŸã
            if (formData.subType === productId) {
                setFormData({
                    ...formData,
                    subType: '',
                    productName: ''
                });
            }
        } catch (error) {
            console.error("Error deleting custom product:", error);
        }
    };

    const handleSubTypeClick = (subType, customName = '') => {
        // ÿ•ÿ∞ÿß ÿ™ŸÖ ÿßŸÑÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ÿ≤ÿ± "ŸÖŸÜÿ™ÿ¨ ŸÖÿÆÿµÿµ"
        if (subType === 'custom' && !customName) {
            setShowCustomInput(true);
            // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑŸÇŸäŸÖ ÿπŸÜÿØ ÿßŸÑÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ÿ≤ÿ± ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ
            setFormData({
                ...formData,
                subType: '',
                productName: ''
            });
            return;
        }
    
        setShowCustomInput(false);
        
        if (customName) {
            // ÿπŸÜÿØ ÿßŸÑÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ŸÖŸÜÿ™ÿ¨ ŸÖÿÆÿµÿµ ŸÖŸàÿ¨ŸàÿØ
            setFormData({
                ...formData,
                subType: customName,
                productName: customName
            });
        } else {
            // ŸÑŸÑÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
            setFormData({
                ...formData,
                subType,
                productName: subType === 'crew' ? 'ŸÉÿ±Ÿà' :
                            subType === 'vbucks' ? 'ŸÅŸäÿ®ŸàŸÉÿ≥' :
                            subType === 'netflix' ? 'ŸÜÿ™ŸÅŸÑŸÉÿ≥' :
                            subType === 'shahid' ? 'ÿ¥ÿßŸáÿØ' :
                            subType === 'prime' ? 'ÿ®ÿ±ÿßŸäŸÖ' :
                            subType === 'promotion' ? 'ÿ™ÿ±ŸàŸäÿ¨' :
                            subType === 'video' ? 'ŸÅŸäÿØŸäŸà ÿ•ÿπŸÑÿßŸÜŸä' : ''
            });
        }
    };

    const getSubTypeButtons = () => {
        switch(formData.serviceType) {
            case 'games':
                return (
                    <div className="sub-type-buttons">
                        {/* ÿßŸÑÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© */}
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'crew' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('crew')}
                        >
                            <span>üë•</span>
                            <span>ŸÉÿ±Ÿà</span>
                        </button>
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'vbucks' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('vbucks')}
                        >
                            <span>üíé</span>
                            <span>ŸÅŸäÿ®ŸàŸÉÿ≥</span>
                        </button>
                        {/* ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿßŸÑŸÖÿÆÿµÿµÿ© */}
                        {customProducts.games.map(product => (
                            <button
                                key={product.id}
                                type="button"
                                className={`sub-type-btn custom-product ${formData.subType === product.name ? 'active' : ''}`}
                                onClick={() => handleSubTypeClick('custom', product.name)}
                            >
                                <span>üéÆ</span>
                                <span>{product.name}</span>
                                <span 
                                    className="delete-custom-product"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        handleCustomProductDelete(product.id);
                                        // ÿ•ÿ∞ÿß ŸÉÿßŸÜ Ÿáÿ∞ÿß ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ ŸáŸà ÿßŸÑŸÖÿ≠ÿØÿØ ÿ≠ÿßŸÑŸäÿßŸãÿå ŸÇŸÖ ÿ®ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ≠ŸÇŸàŸÑ
                                        if (formData.subType === product.name) {
                                            setFormData({
                                                ...formData,
                                                subType: '',
                                                productName: ''
                                            });
                                        }
                                    }}
                                >
                                    √ó
                                </span>
                            </button>
                        ))}
                        {/* ÿ≤ÿ± ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ */}
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'custom' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('custom')}
                        >
                            <span>‚ú®</span>
                            <span>ŸÖŸÜÿ™ÿ¨ ŸÖÿÆÿµÿµ</span>
                        </button>
                        {/* ÿ≠ŸÇŸÑ ÿ•ÿØÿÆÿßŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ */}
                        {showCustomInput && (
                            <div className="custom-product-input">
                                <input
                                    type="text"
                                    value={customProductName}
                                    onChange={(e) => setCustomProductName(e.target.value)}
                                    placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ"
                                    className="form-input"
                                />
                                <button
                                    type="button"
                                    onClick={handleCustomProductAdd}
                                    className="add-custom-btn"
                                >
                                    ÿ•ÿ∂ÿßŸÅÿ©
                                </button>
                            </div>
                        )}
                    </div>
                );
            case 'subscriptions':
                return (
                    <div className="sub-type-buttons">
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'netflix' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('netflix')}
                        >
                            <span>üé¨</span>
                            <span>ŸÜÿ™ŸÅŸÑŸÉÿ≥</span>
                        </button>
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'shahid' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('shahid')}
                        >
                            <span>üì∫</span>
                            <span>ÿ¥ÿßŸáÿØ</span>
                        </button>
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'prime' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('prime')}
                        >
                            <span>üõí</span>
                            <span>ÿ®ÿ±ÿßŸäŸÖ</span>
                        </button>
                        {/* ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿßŸÑŸÖÿÆÿµÿµÿ© ŸÑŸÑÿßÿ¥ÿ™ÿ±ÿßŸÉÿßÿ™ */}
                        {customProducts.subscriptions.map(product => (
                            <button
                                key={product.id}
                                type="button"
                                className={`sub-type-btn custom-product ${formData.subType === product.name ? 'active' : ''}`}
                                onClick={() => handleSubTypeClick('custom', product.name)}
                            >
                                <span>üéØ</span>
                                <span>{product.name}</span>
                                <span 
                                    className="delete-custom-product"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        handleCustomProductDelete(product.id);
                                        if (formData.subType === product.name) {
                                            setFormData({
                                                ...formData,
                                                subType: '',
                                                productName: ''
                                            });
                                        }
                                    }}
                                >
                                    √ó
                                </span>
                            </button>
                        ))}
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'custom' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('custom')}
                        >
                            <span>‚ú®</span>
                            <span>ŸÖŸÜÿ™ÿ¨ ŸÖÿÆÿµÿµ</span>
                        </button>
                        {showCustomInput && (
                            <div className="custom-product-input">
                                <input
                                    type="text"
                                    value={customProductName}
                                    onChange={(e) => setCustomProductName(e.target.value)}
                                    placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ"
                                    className="form-input"
                                />
                                <button
                                    type="button"
                                    onClick={handleCustomProductAdd}
                                    className="add-custom-btn"
                                >
                                    ÿ•ÿ∂ÿßŸÅÿ©
                                </button>
                            </div>
                        )}
                    </div>
                );
            case 'services':
                return (
                    <div className="sub-type-buttons">
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'promotion' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('promotion')}
                        >
                            <span>üì¢</span>
                            <span>ÿ™ÿ±ŸàŸäÿ¨</span>
                        </button>
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'video' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('video')}
                        >
                            <span>üé•</span>
                            <span>ŸÅŸäÿØŸäŸà ÿ•ÿπŸÑÿßŸÜŸä</span>
                        </button>
                        {/* ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿßŸÑŸÖÿÆÿµÿµÿ© ŸÑŸÑÿÆÿØŸÖÿßÿ™ */}
                        {customProducts.services.map(product => (
                            <button
                                key={product.id}
                                type="button"
                                className={`sub-type-btn custom-product ${formData.subType === product.name ? 'active' : ''}`}
                                onClick={() => handleSubTypeClick('custom', product.name)}
                            >
                                <span>‚ö°</span>
                                <span>{product.name}</span>
                                <span 
                                    className="delete-custom-product"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        handleCustomProductDelete(product.id);
                                        if (formData.subType === product.name) {
                                            setFormData({
                                                ...formData,
                                                subType: '',
                                                productName: ''
                                            });
                                        }
                                    }}
                                >
                                    √ó
                                </span>
                            </button>
                        ))}
                        <button
                            type="button"
                            className={`sub-type-btn ${formData.subType === 'custom' ? 'active' : ''}`}
                            onClick={() => handleSubTypeClick('custom')}
                        >
                            <span>‚ú®</span>
                            <span>ŸÖŸÜÿ™ÿ¨ ŸÖÿÆÿµÿµ</span>
                        </button>
                        {showCustomInput && (
                            <div className="custom-product-input">
                                <input
                                    type="text"
                                    value={customProductName}
                                    onChange={(e) => setCustomProductName(e.target.value)}
                                    placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿßŸÑŸÖÿÆÿµÿµ"
                                    className="form-input"
                                />
                                <button
                                    type="button"
                                    onClick={handleCustomProductAdd}
                                    className="add-custom-btn"
                                >
                                    ÿ•ÿ∂ÿßŸÅÿ©
                                </button>
                            </div>
                        )}
                    </div>
                );
            default:
                return null;
        }
    };

    const renderContent = () => {
        switch (activeSection) {
            case 'orders':
                return (
                    <div className="orders-section">
                        <div className="section-header">
                            <i className="section-icon">üì¶</i>
                            <h2>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ŸàÿßŸÑÿ∑ŸÑÿ®ÿßÿ™</h2>
                        </div>
                        <form className="order-form" onSubmit={handleSubmit}>
                            <div className="service-types">
                                <button
                                    type="button"
                                    className={`service-btn ${formData.serviceType === 'games' ? 'active' : ''}`}
                                    onClick={() => setFormData({...formData, serviceType: 'games'})}
                                >
                                    <span>üéÆ</span>
                                    <span>ÿ£ŸÑÿπÿßÿ®</span>
                                </button>
                                <button
                                    type="button"
                                    className={`service-btn ${formData.serviceType === 'subscriptions' ? 'active' : ''}`}
                                    onClick={() => setFormData({...formData, serviceType: 'subscriptions'})}
                                >
                                    <span>üéØ</span>
                                    <span>ÿßÿ¥ÿ™ÿ±ÿßŸÉÿßÿ™</span>
                                </button>
                                <button
                                    type="button"
                                    className={`service-btn ${formData.serviceType === 'services' ? 'active' : ''}`}
                                    onClick={() => setFormData({...formData, serviceType: 'services'})}
                                >
                                    <span>‚ö°</span>
                                    <span>ÿÆÿØŸÖÿßÿ™</span>
                                </button>
                            </div>
                            {getSubTypeButtons()}
                            <div className="form-group">
                                <label>ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨</label>
                                <input
                                    type="text"
                                    name="productName"
                                    className="form-input"
                                    value={formData.productName}
                                    onChange={handleInputChange}
                                    placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨"
                                />
                            </div>

                            <div className="form-group">
                                <label>ÿ≥ÿπÿ± ÿßŸÑÿ™ŸÉŸÑŸÅÿ©</label>
                                <input
                                    type="number"
                                    name="costPrice"
                                    className="form-input"
                                    value={formData.costPrice}
                                    onChange={handleInputChange}
                                    placeholder="ÿ≥ÿπÿ± ÿßŸÑÿ™ŸÉŸÑŸÅÿ©"
                                />
                            </div>

                            <div className="form-group">
                                <label>ÿ≥ÿπÿ± ÿßŸÑÿ®Ÿäÿπ</label>
                                <input
                                    type="number"
                                    name="sellingPrice"
                                    className="form-input"
                                    value={formData.sellingPrice}
                                    onChange={handleInputChange}
                                    placeholder="ÿ≥ÿπÿ± ÿßŸÑÿ®Ÿäÿπ"
                                />
                            </div>

                            <div className="form-group description-group">
                                <label>ŸàÿµŸÅ ÿßŸÑŸÖŸÜÿ™ÿ¨</label>
                                <textarea
                                    name="description"
                                    className="form-input"
                                    value={formData.description}
                                    onChange={handleInputChange}
                                    placeholder="ŸàÿµŸÅ ÿ™ŸÅÿµŸäŸÑŸä ŸÑŸÑŸÖŸÜÿ™ÿ¨"
                                    rows="4"
                                ></textarea>
                            </div>

                            <button type="submit" className="submit-btn">
                                ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨
                            </button>
                        </form>
                    </div>
                );
            case 'capital':
                return <div className="capital-section">
                    <h2>ÿ•ÿØÿßÿ±ÿ© ÿ±ÿ£ÿ≥ ÿßŸÑŸÖÿßŸÑ</h2>
                    <div className="capital-stats">
                        <div class="stat-card">
                            <h3>ÿ±ÿ£ÿ≥ ÿßŸÑŸÖÿßŸÑ ÿßŸÑÿ≠ÿßŸÑŸä</h3>
                            <p class="amount">50,000 ÿØ.ÿπ</p>
                        </div>
                        <div class="stat-card">
                            <h3>ÿßŸÑÿ£ÿ±ÿ®ÿßÿ≠</h3>
                            <p class="amount positive">+15,000 ÿØ.ÿπ</p>
                        </div>
                    </div>
                </div>;
            case 'analytics':
                return <div className="analytics-section">
                    <h2>ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™</h2>
                    <div className="analytics-cards">
                        <div class="stat-card">
                            <h3>ÿπÿØÿØ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸäŸàŸÖ</h3>
                            <p>25</p>
                        </div>
                        <div class="stat-card">
                            <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑŸÖÿ®Ÿäÿπÿßÿ™</h3>
                            <p>150,000 ÿØ.ÿπ</p>
                        </div>
                    </div>
                </div>;
            case 'archive':
                return <div className="archive-section">
                    <h2>ÿ£ÿ±ÿ¥ŸäŸÅ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™</h2>
                    <div className="archive-filters">
                        <input type="date" />
                        <select>
                            <option value="all">ŸÉŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™</option>
                            <option value="completed">ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©</option>
                            <option value="cancelled">ÿßŸÑŸÖŸÑÿ∫Ÿäÿ©</option>
                        </select>
                    </div>
                </div>;
            default:
                return <div>ÿßÿÆÿ™ÿ± ŸÇÿ≥ŸÖÿßŸã</div>;
        }
    };

    return (
        <div className="dashboard">
            <nav className="dashboard-nav">
                {Object.entries(sections).map(([key, { title, icon }]) => (
                    <button
                        key={key}
                        className={`nav-btn ${activeSection === key ? 'active' : ''}`}
                        onClick={() => setActiveSection(key)}
                    >
                        <span>{icon}</span>
                        <span>{title}</span>
                    </button>
                ))}
            </nav>
            <main className="dashboard-content">
                {renderContent()}
            </main>
        </div>
    );
};

export default Dashboard;
