import React, { useState, useEffect } from 'react';
import { db } from '../../firebase/config';
import { collection, addDoc, getDocs, query, where, deleteDoc, doc } from 'firebase/firestore';
import { getDeviceId } from '../../utils/deviceId';
import Dashboard from '../Dashboard/Dashboard';
import RevenueChart from '../Charts/RevenueChart';
import OrderTypesChart from '../Charts/OrderTypesChart';
import OrderDetails from '../Charts/OrderDetails';
import './Analytics.css';

const Analytics = ({ orders }) => {
    const [name, setName] = useState('');
    const [savedName, setSavedName] = useState('');
    const [docId, setDocId] = useState(null);
    const deviceId = getDeviceId();
    const [showWelcome, setShowWelcome] = useState(true);

    const authorizedUsers = [
        { names: ['ÿ¥ŸÖÿ≥', 'ÿ¥ŸÖŸàÿ≥', 'shams', 'shmoos'], type: 'shams' },
        { names: ['ÿ®ŸÜŸäŸÜ', 'ÿ®ŸÜŸàŸÜŸá', 'baneen'], type: 'baneen' },
        { names: ['ÿπŸÑŸä', 'ÿπŸÑŸâ', 'ali'], type: 'ali' },
        { names: ['ŸÖÿ¨ÿ™ÿ®Ÿâ', 'ŸÖÿ¨ÿ™ÿ®ÿß', 'mojtaba'], type: 'mojtaba' }
    ];

    const isAuthorized = (name) => {
        const lowerName = name.toLowerCase().trim();
        return authorizedUsers.some(user => user.names.includes(lowerName));
    };

    const getUserType = (name) => {
        const lowerName = name.toLowerCase().trim();
        return authorizedUsers.find(user => 
            user.names.includes(lowerName))?.type || 'unauthorized';
    };

    useEffect(() => {
        const fetchName = async () => {
            const q = query(collection(db, 'users'), where('deviceId', '==', deviceId));
            const querySnapshot = await getDocs(q);
            querySnapshot.forEach((doc) => {
                setSavedName(doc.data().name);
                setDocId(doc.id);
            });
        };
        fetchName();
    }, [deviceId]);

    useEffect(() => {
        if (savedName) {
            const timer = setTimeout(() => {
                setShowWelcome(false);
            }, 2000);
            return () => clearTimeout(timer);
        }
    }, [savedName]);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, 'orders'));
                const ordersData = querySnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data(),
                }));
                setOrders(ordersData);
                console.log("Fetched orders:", ordersData);
            } catch (error) {
                console.error("Error fetching orders:", error);
            }
        };

        fetchOrders();
    }, []);

    const getWelcomeMessage = (name) => {
        if (!isAuthorized(name)) {
            return (
                <div className="welcome-special unauthorized-animation">
                    <h1>‚õî ÿπÿ∞ÿ±ÿßŸã! ÿ£ŸÜÿ™ ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ŸÑŸÉ ÿ®ÿßŸÑÿØÿÆŸàŸÑ ‚õî</h1>
                    <div className="floating-emojis">
                        <span>üö´</span>
                        <span>‚õî</span>
                        <span>üöß</span>
                        <span>‚ö†Ô∏è</span>
                    </div>
                    <p className="unauthorized-message">
                        Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ŸÖÿÆÿµÿµ ŸÅŸÇÿ∑ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ÿßŸÑŸÖÿµÿ±ÿ≠ ŸÑŸáŸÖ
                    </p>
                </div>
            );
        }

        const userType = getUserType(name);
        switch (userType) {
            case 'shams':
                return (
                    <div className="welcome-special shams-animation">
                        <h1>‚ú® ÿ¥ŸÖŸàÿ≥ÿ≥ÿ≥ ÿßŸÑÿ∫ÿßŸÑŸä ÿßŸáŸÑÿß ‚ú®</h1>
                        <div className="floating-emojis">
                            <span>üåü</span>
                            <span>‚≠ê</span>
                            <span>üí´</span>
                            <span>üåû</span>
                        </div>
                    </div>
                );
            case 'baneen':
                return (
                    <div className="welcome-special baneen-animation">
                        <h1>üíù ŸáŸÑÿßŸàŸàŸàŸà ÿ®ŸÜŸàŸÜŸá üíù</h1>
                        <div className="floating-emojis">
                            <span>üéÄ</span>
                            <span>üíñ</span>
                            <span>‚ú®</span>
                            <span>ü¶ã</span>
                        </div>
                    </div>
                );
            case 'ali':
                return (
                    <div className="welcome-special ali-animation">
                        <h1>üåü ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿπŸÑÿßŸàŸäŸäŸäŸä üåü</h1>
                        <div className="floating-emojis">
                            <span>üí™</span>
