import React, { useState, useEffect } from 'react';
import { db } from '../../firebase/config';
import { collection, addDoc, getDocs, query, where, deleteDoc, doc } from 'firebase/firestore';
import { getDeviceId } from '../../utils/deviceId';
import Dashboard from '../Dashboard/Dashboard';
import './Analytics.css';

const Analytics = () => {
    const [name, setName] = useState('');
    const [savedName, setSavedName] = useState('');
    const [docId, setDocId] = useState(null);
    const deviceId = getDeviceId();
    const [showWelcome, setShowWelcome] = useState(true);

    const authorizedUsers = [
        { names: ['ÿ¥ŸÖÿ≥', 'ÿ¥ŸÖŸàÿ≥', 'shams', 'shmoos'], type: 'shams' },
        { names: ['ÿ®ŸÜŸäŸÜ', 'ÿ®ŸÜŸàŸÜŸá', 'baneen'], type: 'baneen' },
        { names: ['ÿπŸÑŸä', 'ÿπŸÑŸâ', 'ali'], type: 'ali' },
        { names: ['ŸÖÿ¨ÿ™ÿ®Ÿâ', 'ŸÖÿ¨ÿ™ÿ®ÿß', 'mojtaba'], type: 'mojtaba' }
    ];

    const isAuthorized = (name) => {
        const lowerName = name.toLowerCase().trim();
        return authorizedUsers.some(user => user.names.includes(lowerName));
    };

    const getUserType = (name) => {
        const lowerName = name.toLowerCase().trim();
        return authorizedUsers.find(user => 
            user.names.includes(lowerName))?.type || 'unauthorized';
    };

    useEffect(() => {
        const fetchName = async () => {
            const q = query(collection(db, 'users'), where('deviceId', '==', deviceId));
            const querySnapshot = await getDocs(q);
            querySnapshot.forEach((doc) => {
                setSavedName(doc.data().name);
                setDocId(doc.id);
            });
        };
        fetchName();
    }, [deviceId]);

    useEffect(() => {
        if (savedName) {
            const timer = setTimeout(() => {
                setShowWelcome(false);
            }, 2000);
            return () => clearTimeout(timer);
        }
    }, [savedName]);

    const getWelcomeMessage = (name) => {
        if (!isAuthorized(name)) {
            return (
                <div className="welcome-special unauthorized-animation">
                    <h1>‚õî ÿπÿ∞ÿ±ÿßŸã! ÿ£ŸÜÿ™ ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ŸÑŸÉ ÿ®ÿßŸÑÿØÿÆŸàŸÑ ‚õî</h1>
                    <div className="floating-emojis">
                        <span>üö´</span>
                        <span>‚õî</span>
                        <span>üöß</span>
                        <span>‚ö†Ô∏è</span>
                    </div>
                    <p className="unauthorized-message">
                        Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ŸÖÿÆÿµÿµ ŸÅŸÇÿ∑ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ÿßŸÑŸÖÿµÿ±ÿ≠ ŸÑŸáŸÖ
                    </p>
                </div>
            );
        }

        const userType = getUserType(name);
        switch (userType) {
            case 'shams':
                return (
                    <div className="welcome-special shams-animation">
                        <h1>‚ú® ÿ¥ŸÖŸàÿ≥ÿ≥ÿ≥ ÿßŸÑÿ∫ÿßŸÑŸä ÿßŸáŸÑÿß ‚ú®</h1>
                        <div className="floating-emojis">
                            <span>üåü</span>
                            <span>‚≠ê</span>
                            <span>üí´</span>
                            <span>üåû</span>
                        </div>
                    </div>
                );
            case 'baneen':
                return (
                    <div className="welcome-special baneen-animation">
                        <h1>üíù ŸáŸÑÿßŸàŸàŸàŸà ÿ®ŸÜŸàŸÜŸá üíù</h1>
                        <div className="floating-emojis">
                            <span>üéÄ</span>
                            <span>üíñ</span>
                            <span>‚ú®</span>
                            <span>ü¶ã</span>
                        </div>
                    </div>
                );
            case 'ali':
                return (
                    <div className="welcome-special ali-animation">
                        <h1>üåü ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿπŸÑÿßŸàŸäŸäŸäŸä üåü</h1>
                        <div className="floating-emojis">
                            <span>üí™</span>
                            <span>üëë</span>
                            <span>üéâ</span>
                            <span>‚ö°</span>
                        </div>
                    </div>
                );
            case 'mojtaba':
                return (
                    <div className="welcome-special mojtaba-animation">
                        <h1>üåô ÿ£ŸáŸÑÿßŸã ŸÖÿ¨ÿ™ÿ®Ÿâ üåô</h1>
                        <div className="floating-emojis">
                            <span>üåô</span>
                            <span>‚ú®</span>
                            <span>üåü</span>
                            <span>üí´</span>
                        </div>
                    </div>
                );
            default:
                return null;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!isAuthorized(name)) {
            setSavedName(name); // Still set the name to show unauthorized message
            return;
        }

        try {
            const docRef = await addDoc(collection(db, 'users'), {
                name: name,
                deviceId: deviceId,
                timestamp: new Date()
            });
            setSavedName(name);
            setDocId(docRef.id);
            setName('');
        } catch (error) {
            console.error("Error adding name: ", error);
        }
    };

    const handleRemove = async () => {
        try {
            if (docId) {
                await deleteDoc(doc(db, 'users', docId));
                setSavedName('');
                setDocId(null);
            }
        } catch (error) {
            console.error("Error removing name: ", error);
        }
    };

    const UserUnauthorizedScreen = () => (
        <div className="unauthorized-container">
            <div className="welcome-special unauthorized-animation">
                <h1>‚õî ÿπÿ∞ÿ±ÿßŸã! ÿ£ŸÜÿ™ ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ŸÑŸÉ ÿ®ÿßŸÑÿØÿÆŸàŸÑ ‚õî</h1>
                <div className="floating-emojis">
                    <span>üö´</span>
                    <span>‚õî</span>
                    <span>üöß</span>
                    <span>‚ö†Ô∏è</span>
                </div>
                <p className="unauthorized-message">
                    Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ŸÖÿÆÿµÿµ ŸÅŸÇÿ∑ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ÿßŸÑŸÖÿµÿ±ÿ≠ ŸÑŸáŸÖ
                </p>
                <button 
                    className="reset-btn"
                    onClick={handleRemove}
                >
                    ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
                </button>
            </div>
        </div>
    );

    return (
        <div className="analytics-container">
            {!savedName ? (
                <form onSubmit={handleSubmit} className="name-form">
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖŸÉ ŸáŸÜÿß"
                        className="name-input arabic-input"
                    />
                    <button type="submit" className="submit-btn">ÿ™ŸÖ</button>
                </form>
            ) : showWelcome ? (
                <div className="welcome-container">
                    {getWelcomeMessage(savedName)}
                </div>
            ) : (
                isAuthorized(savedName) ? <Dashboard /> : <UserUnauthorizedScreen />
            )}
        </div>
    );
};

export default Analytics;
